document.querySelector(".order").addEventListener("submit",(function(e){e.preventDefault();var t=new FormData(this);fetch("https://jsonplaceholder.typicode.com/posts",{method:"post",headers:{"Content-type":"application/x-www-form-urlencoded; charset=UTF-8"},body:t}).then((e=>e.json())).then((e=>{101===e.id&&(document.querySelector(".info__container").innerText="Удачно Отправлено"),console.log(e)})).catch((e=>console.log(e)))}));